// Code11 â€” Robust "unsuccessful call" detector for n8n

// ---------- helpers ----------
const pick = (obj, paths, fallback = undefined) => {
  for (const p of paths) {
    try {
      const v = p.split('.').reduce((o, k) => o?.[k], obj);
      if (v !== undefined && v !== null) return v;
    } catch {}
  }
  return fallback;
};

const toLower = (x) => (typeof x === 'string' ? x.toLowerCase() : '');

// ---------- pull common fields from many shapes ----------
const body = $json.body ?? $json ?? {};
const call  = pick(body, ['call','payload.call','data.call','event.call']) || body;

const transcript =
  pick(body, [
    'call.call_analysis.transcript',
    'call.CallAnalysis.transcript',
    'call.transcript',
    'transcript',
    'payload.transcript',
    'data.transcript'
  ], '') || '';

const normalizedTranscript = toLower(transcript);

// disconnection / status from various places
const disconnectionReason =
  toLower(
    pick(call, ['disconnection_reason','ended_reason','hangup_cause','hangup_reason']) ||
    pick(body, ['disconnection_reason','reason']) ||
    ''
  );

const status = toLower(
  pick(call, ['status','call_status','state']) ||
  pick(body, ['status','call_status','state']) ||
  ''
);

const inVoicemailFlag = !!(
  pick(call, ['call_analysis.in_voicemail','in_voicemail']) ??
  pick(body, ['in_voicemail'])
);

const answeredBy = toLower(
  pick(call, ['answered_by','machine_detection_result','amd_result']) || ''
);

// durations in sec
let durationSec =
  Number(pick(call, ['duration_sec','call_duration','total_duration'])) ||
  (Number(pick(call, ['duration_ms'])) ? Number(pick(call, ['duration_ms']))/1000 : 0);

// fallback: compute from timestamps if needed
if (!durationSec) {
  const start = pick(call, ['start_timestamp','started_at']);
  const end   = pick(call, ['end_timestamp','ended_at']);
  if (start && end) {
    const s = new Date(start).getTime();
    const e = new Date(end).getTime();
    if (!Number.isNaN(s) && !Number.isNaN(e) && e > s) {
      durationSec = Math.round((e - s) / 1000);
    }
  }
}

// outcome hints (if upstream analyzer already set something)
const outcomePayload =
  pick(call, ['call_analysis.custom_analysis_data.Outcome']) ||
  pick(call, ['CallAnalysis.custom_analysis_data.Outcome']) ||
  pick(body, ['Outcome','outcome']) ||
  '';

const outcomeLower = toLower(outcomePayload);

// ---------- classify granular flags ----------
const reasonStr = `${disconnectionReason} ${status} ${answeredBy} ${normalizedTranscript}`;

// Busy / No answer / Failed / Rejected / Invalid
const isBusy =
  /\bbusy\b|dial_busy|user_busy/.test(reasonStr);

const isNoAnswer =
  /no[\s\-]?answer|dial_no_answer|timeout|ring[-\s]?timeout|not\s*answered/.test(reasonStr);

const isFailedNetwork =
  /network|system|signaling|unavailable|gateway|failed|congestion|error|invalid[_\s-]?number|not\s*reachable/.test(reasonStr);

const isRejected =
  /rejected|declined|blocked|dnd|do[-\s]?not[-\s]?disturb/.test(reasonStr);

const amdMachine =
  /machine|voicemail|answering\s*machine/.test(answeredBy);

// Voicemail detection (explicit flag + transcript cues)
const vmCues = [
  'voicemail','voice mail','leave a message','after the tone','beep',
  'mailbox is full','cannot take your call','not available to take your call'
];
const isVoicemail =
  inVoicemailFlag || vmCues.some(c => normalizedTranscript.includes(c));

// Human speech heuristic (do we see real user utterances?)
const hasUserPrefix = /\buser:\s*\S+/i.test(transcript);
const humanWordCues = /(\bhello\b|\bhi\b|\byes\b|\bspeaking\b|\bthis is\b|\bi want\b|\bplease\b|\bokay\b)/i;
const hasHumanSpeech = hasUserPrefix || humanWordCues.test(transcript);

// Very short calls with no human speech
const shortCall = (durationSec > 0 && durationSec < 8 && !hasHumanSpeech);

// Outcome-based positives
const outcomePositive =
  /\bbook|appointment|schedule|meeting|interested|qualified|connected|human\b/.test(outcomeLower);

// If payload says machine, treat as machine even if transcript has words like "leave a message"
const isMachine = amdMachine || (isVoicemail && !hasHumanSpeech);

// ---------- final decision ----------
// A call is "unsuccessful" when:
// - voicemail OR machine detection OR busy OR no answer OR failed network OR rejected
// - OR it's a very short call with no human speech
// BUT if analyzer already declared a positive outcome, override to successful.
let isUnsuccessful =
  isVoicemail ||
  isMachine ||
  isBusy ||
  isNoAnswer ||
  isFailedNetwork ||
  isRejected ||
  shortCall;

if (outcomePositive) {
  isUnsuccessful = false;
}

// ---------- optional debug (safe extra fields) ----------
const debug = {
  status,
  disconnectionReason,
  answeredBy,
  durationSec,
  flags: {
    isVoicemail,
    isMachine,
    isBusy,
    isNoAnswer,
    isFailedNetwork,
    isRejected,
    shortCall,
    hasHumanSpeech,
    outcomePositive
  },
  outcomePayload
};

// Return original payload plus the decision
return {
