  isUnsuccessful,
  // comment out 'debug' if you don't want extra fields going downstream
  debug
};

// ---------- Helpers ----------
const get = (obj, paths) => {
  for (const p of paths) {
    try {
      const v = p.split('.').reduce((o,k)=>o?.[k], obj);
      if (v !== undefined && v !== null) return v;
    } catch {}
  }
  return undefined;
};

const now = new Date();

// ---------- 1) Pull raw fields from many possible shapes ----------
const body = $json.body ?? $json ?? {};
const callObj = get(body, [
  'call', 'payload.call', 'data.call', 'event.call'
]) || body;

const transcriptRaw =
  get(body, [
    'call.call_analysis.transcript',
