  get(body, [
    'call.call_analysis.custom_analysis_data.Outcome',
    'call.CallAnalysis.custom_analysis_data.Outcome',
    'call.custom_analysis_data.Outcome',
    'Outcome',
  ]);

const inVoicemailFlag =
  !!get(body, [
    'call.call_analysis.in_voicemail',
    'call.CallAnalysis.in_voicemail',
    'in_voicemail'
  ]);

const bookingTimeRaw =
  get($json, ['booking_response.time', 'booking.time']) ||
  get(body, ['booking_response.time', 'booking.time']);

const agentId =
  get(body, ['call.agent_id', 'agent_id']) || null;

const retellCallId =
  get(body, ['call.call_id','call.id','retell_call_id']) || null;

const startTs =
  get(body, ['call.start_timestamp','start_timestamp','call.started_at']) || null;

const endTs =
  get(body, ['call.end_timestamp','end_timestamp','call.ended_at']) || null;

const disconnectionReason =
  get(body, ['call.disconnection_reason','disconnection_reason','reason']) || null;

const phoneNumber =
  get(body, ['call.to_number','to_number','phone','phone_number']) || null;

const fromEmailIncoming =
  get($json, ['from_email','email.from','sender']) ||
  get(body, ['from_email','email.from','sender']) || null;

const prospectId =
  get($json, ['prospect_id','prospect.id']) ||
  get(body, ['prospect_id','prospect.id']) || null;

// ---------- 2) Normalize transcript ----------
const transcript = (transcriptRaw || "").toString();
const tLower = transcript.toLowerCase();

// ---------- 3) Robust voicemail detection ----------
let isVoicemail = !!inVoicemailFlag;
if (!isVoicemail) {
  const vmHints = [
    'voicemail','voice mail','leave a message','beep','after the tone',
